mapper.select:
SELECT 

FROM activity_info a

WHERE a.id = #{id}

SELECT 

FROM activity_info a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.activity_name LIKE 
			'%'||#{activityName}||'%'
			'%'+#{activityName}+'%'
			concat('%',#{activityName},'%')


		AND a.status = #{status}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM activity_info a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE activity_info SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
