mapper.select:
SELECT 

FROM index_billboard a

WHERE a.id = #{id}

SELECT 

FROM index_billboard a




		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')


		AND a.app_flag = #{appFlag}


		AND a.type = #{type}


		AND a.scale = #{scale}




		ORDER BY ${page.orderBy}


		ORDER BY a.sort asc
