mapper.select:
SELECT 

FROM coupon_config a

WHERE a.id = #{id}

SELECT 

FROM coupon_config a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.coupon_type = #{couponType}


		AND a.coupon_name LIKE 
			'%'||#{couponName}||'%'
			'%'+#{couponName}+'%'
			concat('%',#{couponName},'%')


		AND a.status = #{status}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM coupon_config a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE coupon_config SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
