mapper.select:
SELECT 

FROM gift_config_category a

WHERE a.id = #{id}

SELECT
FROM gift_config_category a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.category_name LIKE '%'||#{categoryName}||'%' '%'+#{categoryName}+'%' concat('%',#{categoryName},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM gift_config_category a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE gift_config_category SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
