mapper.select:
SELECT 

FROM gift_config_coupon a

WHERE a.id = #{id}

SELECT 

FROM gift_config_coupon a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.gift_config_id = #{giftConfigId}




		ORDER BY ${page.orderBy}

SELECT 

FROM gift_config_coupon a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE gift_config_coupon SET 	
	gift_config_id = #{giftConfigId},
	coupon_id = #{couponId},
	coupon_count = #{couponCount}
WHERE id = #{id}

UPDATE gift_config_coupon SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE gift_config_id = #{giftConfigId}
