mapper.select:
SELECT 

FROM gift_config a

WHERE a.id = #{id}

SELECT 

FROM gift_config a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.gift_name LIKE 
			'%'||#{giftName}||'%'
			'%'+#{giftName}+'%'
			concat('%',#{giftName},'%')


		AND a.create_date BETWEEN #{beginCreateDate} AND #{endCreateDate}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM gift_config a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE gift_config SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
