mapper.select:
SELECT 

FROM gift_config_goods a

WHERE a.id = #{id}

select a.id AS "id", a.gift_config_id AS "giftConfigId", a.goods_id AS "goodsId", a.goods_count AS "goodsCount", a.merchant_code AS "merchantCode", a.del_flag AS "delFlag", a.goods_standard_id AS "goodsStandardId", s.name AS "standardName", g.goods_name AS "goodsName",
g.goods_price as "goodsPrice",
g.image as "image" from gift_config_goods a left join goods_info g on g.id = a.goods_id left join goods_standard s on s.id = a.goods_standard_id
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.gift_config_id = #{giftConfigId}

SELECT 

FROM gift_config_goods a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.gift_config_id = #{giftConfigId}




		ORDER BY ${page.orderBy}

SELECT 

FROM gift_config_goods a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE gift_config_goods SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE gift_config_id = #{giftConfigId}
