mapper.select:
SELECT 

FROM gift_customer_goods a

WHERE a.id = #{id}

SELECT 

FROM gift_customer_goods a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.gift_customer_id = #{giftCustomerId}




		ORDER BY ${page.orderBy}

SELECT 

FROM gift_customer_goods a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE gift_customer_goods SET 	
	gift_customer_id = #{giftCustomerId},
	goods_id = #{goodsId},
	goods_count = #{goodsCount},
	merchant_code = #{merchantCode}
WHERE id = #{id}

UPDATE gift_customer_goods SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE gift_customer_id = #{giftCustomerId}
