mapper.select:
SELECT 

FROM gift_merchant a

WHERE a.id = #{id}

SELECT 

FROM gift_merchant a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.gift_name LIKE 
			'%'||#{giftName}||'%'
			'%'+#{giftName}+'%'
			concat('%',#{giftName},'%')


		AND a.create_date BETWEEN #{beginCreateDate} AND #{endCreateDate}


		AND a.merchant_code = #{merchantCode}

	and a.gift_count > 0



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM gift_merchant a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE gift_merchant SET 	
	del_flag = #{delFlag}
WHERE id = #{id}

UPDATE gift_merchant SET
	gift_count = #{giftCount}
WHERE id = #{id}

UPDATE gift_merchant SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
