mapper.select:
SELECT 

FROM gift_merchant_goods a

WHERE a.id = #{id}

SELECT 

FROM gift_merchant_goods a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.gift_merchant_id = #{giftMerchantId}




		ORDER BY ${page.orderBy}

SELECT 

FROM gift_merchant_goods a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE gift_merchant_goods SET 	
	gift_merchant_id = #{giftMerchantId},
	goods_id = #{goodsId},
	goods_count = #{goodsCount},
	merchant_code = #{merchantCode}
WHERE id = #{id}

UPDATE gift_merchant_goods SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE gift_merchant_id = #{giftMerchantId}
