mapper.select:
SELECT 

FROM gift_purchase_log a

WHERE a.id = #{id}

SELECT 

FROM gift_purchase_log a

left join sys_user uo on uo.id = m.operator_code
left join sys_office uoo on uoo.id = uo.office_id


		and a.gift_category = #{giftCategory}


		and c.category_name like concat('%',#{giftConfigCategoryName},'%')


		and a.payment_no = #{paymentNo}


		and a.merchant_code = #{merchantCode}


		and a.status = #{status}


		and m.referee = #{merchantId}


		and u.login_name = #{merchantAccount}


		and a.create_date >= #{startDate}


		and a.end_date <= #{endDate}


		and a.pay_time >= #{startPayDate}


		and a.pay_time <= #{endPayDate}


	${sqlMap.dsf}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date DESC
