SELECT

FROM goods_category a




		AND a.parent_category_id = #{parentCategoryId}


		AND a.category_name = #{categoryName}


		AND a.depth = #{depth}


		AND a.status = #{status}


		AND a.create_by = #{createBy.id}


		AND a.create_date = #{createDate}




		ORDER BY ${page.orderBy}


		ORDER BY a.sort asc

SELECT 

FROM goods_category a

	a.id != '0'

		AND a.parent_category_id = #{parentCategoryId}


		AND a.category_name = #{categoryName}


		AND a.depth = #{depth}


		AND a.status = #{status}


		AND a.create_by = #{createBy.id}


		AND a.create_date = #{createDate}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC
