mapper.select:
SELECT 

FROM goods_standard a

WHERE a.id = #{id}

SELECT 

FROM goods_standard a




		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.price = #{price}


		AND a.goods_id = #{goodsId}


		AND a.category_id = #{categoryId}




		ORDER BY ${page.orderBy}

SELECT 

FROM goods_standard a






		ORDER BY ${page.orderBy}

mapper.update:
UPDATE goods_standard SET 	
	name = #{name},
	price = #{price},
	goods_id = #{goodsId},
	category_id = #{categoryId}
WHERE id = #{id}
