mapper.select:
SELECT 

FROM member_feedback a

WHERE a.id = #{id}

SELECT 

FROM member_feedback a




		AND m.customer_name like concat('%',#{shopid},'%')


		and a.customer_code = #{customerCode}


		and m.referee = #{customerId}


		and a.create_date >= #{startDate}


		and a.create_date <= #{endDate}


		and a.reply is null


		and a.reply is not null




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE member_feedback SET 	
	reply = #{reply},
	update_by = #{updateBy.id},
	update_date = #{updateDate}
WHERE id = #{id}
