SELECT
FROM merchant_info_check a
AND a.merchant_name LIKE '%'||#{merchantName}||'%' '%'+#{merchantName}+'%' concat('%',#{merchantName},'%')
AND a.merchant_service_phone LIKE '%'||#{merchantServicePhone}||'%' '%'+#{merchantServicePhone}+'%' concat('%',#{merchantServicePhone},'%')
AND a.check_status = #{checkStatus}
AND a.check_remark = #{checkRemark}
ORDER BY ${page.orderBy}

