mapper.select:
SELECT 

FROM index_notice a

WHERE a.id = #{id}

SELECT 

FROM index_notice a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')


		AND a.create_user = #{createUser}


		AND a.create_date = #{createDate}




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date desc

SELECT 

FROM index_notice a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE index_notice SET 	
	title = #{title},
	content = #{content},
	create_user = #{createUser}
WHERE id = #{id}

UPDATE index_notice SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
